   task read_data;
     integer tdf;
   begin
     tdf = $fopen(TDATA_FILE, "r");
     if (!tdf) begin 
       $display("XTB Error: cannot open %0s file. Exiting...\n", TDATA_FILE);
       $finish; 
      end 
      $fclose(tdf); 
     if (tdfm > 0) $display("XTB: Setting test data file to \"%0s\" (at %0s). Running simulation with new database...", TDATA_FILE, tdfm==1?"compile-time":"runtime");
     else if (verbose >= 1) $display("XTB: Reading test data file \"%0s\" ", TDATA_FILE);
     $readmemb(TDATA_FILE, memall);
   end
    endtask

   task capture ;
     input reg [SIG_IDS-1:0] idargs;
     input reg [2*CUM_WIDTH-1:0] valargs;
   begin
     if (verbose >= 2) $display("XTB: Starting proc capture..., T=%0t, V=%0d", $time, v_count+1);
     //check_sid_args("capture",7'b1, idargs);
     save_context;
     resolve_wft(0);

     assign_val('d`_po , 4'b11XX);

     if (idargs[5] === 1'b1) assign_ival('d`_pi );
     if (idargs[0] === 1'b1) assign_ival('d`a );
     if (idargs[1] === 1'b1) assign_ival('d`b );
     if (idargs[2] === 1'b1) assign_ival('d`ci );
     assign_stmt(the_wft_id);

     if (idargs[6] === 1'b1) assign_ival('d`_po );
     if (idargs[3] === 1'b1) assign_ival('d`s );
     if (idargs[4] === 1'b1) assign_ival('d`co );
     assign_stmt(the_wft_id);

     restore_context;
   end 
   endtask


